// src/screens/ProjectList.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../axios';  // Asegúrate de que axios está configurado correctamente.
import Button from '../components/Button';  // Importar el componente Button
import Input from '../components/Input';  // Asegúrate de importar Input
import Card from '../components/Card';
const ProjectList = () => {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    } else {
      axios.get('/api/projects')
        .then(response => {
          setProjects(response.data);
        })
        .catch(error => {
          console.error('Error al obtener proyectos:', error);
        });
    }
  }, [navigate]);

  const handleSearch = () => {
    axios.get(`/api/projects?search=${search}`)
      .then(response => {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Error al buscar proyectos:', error);
      });
  };

  return (
    <div className="project-list">
      <Button onClick={() => navigate('/projects/create')} className="btn-primary">Crear Proyecto</Button>

      <div className="search-bar">
        <Input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Buscar proyecto"
        />
        <Button onClick={handleSearch} className="btn-secondary">Buscar</Button>
      </div>

      <div className="project-cards">
        {projects.map(project => (
          <Card key={project._id} title={project.title} description={project.description} />
        ))}
      </div>
    </div>
  );
};

export default ProjectList;
