import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../axios';
import Button from '../components/Button';
import Input from '../components/Input';
import Card from '../components/Card';
import './ProjectList.css';  // Agregar estilo CSS si es necesario

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [search, setSearch] = useState('');
  const [showFilter, setShowFilter] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    } else {
      axios.get('/api/projects')
        .then(response => {
          setProjects(response.data);
        })
        .catch(error => {
          console.error('Error al obtener proyectos:', error);
        });
    }
  }, [navigate]);

  const handleSearch = () => {
    axios.get(`/api/projects?search=${search}`)
      .then(response => {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Error al buscar proyectos:', error);
      });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="project-list">
      <div className="header">
        <Button onClick={() => navigate('/projects/create')}>+ Nuevo Proyecto</Button>
        <Button onClick={handleLogout}>Cerrar sesi√≥n</Button>
      </div>
      
      <div className="search-bar">
        <Input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Buscar proyecto"
        />
        <Button onClick={handleSearch}>Buscar</Button>
        <Button onClick={() => setShowFilter(!showFilter)}>Filtrar</Button>
      </div>

      {showFilter && (
        <div className="filter-popup">
          <Input
            type="text"
            placeholder="Buscar por nombre"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <Button onClick={handleSearch}>Aplicar Filtros</Button>
        </div>
      )}

      <div className="project-cards">
        {projects.map(project => (
          <Card key={project._id} title={project.title} description={project.description} />
        ))}
      </div>
    </div>
  );
};

export default ProjectList;
