import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../axios';
import Button from '../components/Button';
import Input from '../components/Input';
import Card from '../components/Card';
import './ProjectList.css';  // Estilos para la lista de proyectos y popup

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [search, setSearch] = useState('');
  const [showFilter, setShowFilter] = useState(false);
  const [showCreateProject, setShowCreateProject] = useState(false);  // Estado para el popup de creación
  const [newProjectData, setNewProjectData] = useState({
    title: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Verificar si el token está presente, si no redirigir al login
  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    } else {
      axios.get('/api/projects')
        .then(response => {
          setProjects(response.data);
        })
        .catch(error => {
          console.error('Error al obtener proyectos:', error);
        });
    }
  }, [navigate]);

  // Buscar proyectos por nombre
  const handleSearch = () => {
    axios.get(`/api/projects?search=${search}`)
      .then(response => {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Error al buscar proyectos:', error);
      });
  };

  // Manejo de log out
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Manejo de cambio en el formulario de nuevo proyecto
  const handleCreateProjectChange = (e) => {
    setNewProjectData({ ...newProjectData, [e.target.name]: e.target.value });
  };

  // Enviar formulario para crear nuevo proyecto
  const handleCreateProjectSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');  // Resetear errores previos

    // Validación de los campos
    if (!newProjectData.title || !newProjectData.description) {
      setError('Por favor ingrese todos los campos');
      setLoading(false);
      return;
    }

    try {
      // Crear el proyecto
      const response = await axios.post('/api/projects', newProjectData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setProjects([response.data, ...projects]);  // Agregar el nuevo proyecto al inicio de la lista
      setShowCreateProject(false);  // Cerrar el popup
      setNewProjectData({ title: '', description: '' });  // Limpiar formulario
    } catch (error) {
      setError('Error al crear proyecto, intente nuevamente');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="project-list">
      <div className="header">
        <Button onClick={() => setShowCreateProject(true)}>+ Nuevo Proyecto</Button>
        <Button onClick={handleLogout}>Cerrar sesión</Button>
      </div>
      
      <div className="search-bar">
        <Input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Buscar proyecto"
        />
        <Button onClick={handleSearch}>Buscar</Button>
        <Button onClick={() => setShowFilter(!showFilter)}>Filtrar</Button>
      </div>

      {showFilter && (
        <div className="filter-popup">
          <Input
            type="text"
            placeholder="Buscar por nombre"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <Button onClick={handleSearch}>Aplicar Filtros</Button>
        </div>
      )}

      {/* Pop-up para crear nuevo proyecto */}
      {showCreateProject && (
        <div className="create-project-popup">
          <div className="popup-overlay" onClick={() => setShowCreateProject(false)}></div>
          <div className="popup-content">
            <h3>Crear Nuevo Proyecto</h3>
            {error && <p className="text-red-500 text-sm">{error}</p>}
            <form onSubmit={handleCreateProjectSubmit}>
              <Input
                type="text"
                name="title"
                value={newProjectData.title}
                onChange={handleCreateProjectChange}
                placeholder="Título del proyecto"
                className="mb-4"
              />
              <Input
                type="text"
                name="description"
                value={newProjectData.description}
                onChange={handleCreateProjectChange}
                placeholder="Descripción del proyecto"
                className="mb-4"
              />
              <Button type="submit" className="w-full" disabled={loading}>
                {loading ? 'Creando...' : 'Crear Proyecto'}
              </Button>
              <Button
                type="button"
                className="w-full mt-2 bg-gray-500 hover:bg-gray-700"
                onClick={() => setShowCreateProject(false)}
              >
                Cancelar
              </Button>
            </form>
          </div>
        </div>
      )}

      <div className="project-cards">
        {projects.map(project => (
          <Card
            key={project._id}
            title={project.title}
            description={project.description}
            onClick={() => navigate(`/projects/${project._id}`)}
          />
        ))}
      </div>
    </div>
  );
};

export default ProjectList;
