import React, { useState, useEffect } from 'react';
import axios from '../axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  useEffect(() => {
    // Verifica si ya hay un token en el localStorage
    if (localStorage.getItem('token')) {
      navigate('/projects'); // Si está autenticado, redirige a proyectos
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Enviar datos de login
      const response = await axios.post('/api/auth/login', formData);
      localStorage.setItem('token', response.data.token); // Guardar el token en el localStorage
      alert('Inicio de sesión exitoso');
      navigate('/projects'); // Redirigir a la lista de proyectos
    } catch (error) {
      alert('Error al iniciar sesión');
    }
  };

  return (
    <div>
      <h2>Iniciar sesión</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Correo electrónico"
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Contraseña"
        />
        <button type="submit">Iniciar sesión</button>
      </form>
      <button onClick={() => navigate('/register')}>Registrarse</button>
    </div>
  );
};

export default Login;
