import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Cambié useHistory por useNavigate

const ProjectCreate = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Usamos useNavigate de react-router-dom v6

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Verifica que los campos estén completos
    if (!title || !description) {
      setErrorMessage('Por favor, completa todos los campos.');
      return;
    }

    try {
      const token = localStorage.getItem('token'); // O donde estés almacenando el JWT

      const response = await axios.post(
        'http://localhost:5000/api/projects',
        { title, description },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Enviar el token JWT en los headers
          },
        }
      );

      // Si la solicitud fue exitosa, navega al listado de proyectos
      navigate('/projects');
    } catch (error) {
      setErrorMessage('Hubo un error al crear el proyecto');
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Crear Proyecto</h1>
      {errorMessage && <p>{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Título</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label>Descripción</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button type="submit">Crear Proyecto</button>
      </form>
    </div>
  );
};

export default ProjectCreate;
