const Button = ({ onClick, children, type = 'button', className = '', size = 'md', variant = 'primary', ...props }) => {
    // Estilos por defecto
    const defaultStyles = {
      primary: 'bg-blue-500 text-white hover:bg-blue-700',
      secondary: 'bg-gray-500 text-white hover:bg-gray-700',
      danger: 'bg-red-500 text-white hover:bg-red-700',
      outline: 'bg-transparent border-2 border-blue-500 text-blue-500 hover:bg-blue-500 hover:text-white',
    };
  
    // Tamaños de botones
    const sizeStyles = {
      sm: 'px-3 py-1 text-sm',
      md: 'px-4 py-2 text-base',  // Tamaño mediano por defecto
      lg: 'px-6 py-3 text-lg',
    };
  
    // Combina las clases con el tipo y tamaño
    const buttonClasses = `${defaultStyles[variant]} ${sizeStyles[size]} rounded ${className}`;
  
    return (
      <button
        type={type}
        onClick={onClick}
        className={buttonClasses}
        {...props}  // Permite pasar propiedades adicionales, como disabled
      >
        {children}
      </button>
    );
  };
  
  export default Button;
