import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../axios'; // Asegúrate de tener la configuración de axios.
import Button from '../components/Button';
import Input from '../components/Input';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      navigate('/projects');
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(''); // Reset previous error

    try {
      const response = await axios.post('/api/auth/login', formData);
      localStorage.setItem('token', response.data.token);
      navigate('/projects');
    } catch (error) {
      setError('Correo o contraseña incorrectos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container flex items-center justify-center h-screen bg-gray-100">
      <div className="login-form bg-white p-8 rounded shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">Iniciar sesión</h2>

        {error && <p className="text-red-500 text-sm mb-4 text-center">{error}</p>}

        <form onSubmit={handleSubmit}>
          <Input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Correo electrónico"
            className="mb-4"
          />
          <Input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Contraseña"
            className="mb-4"
          />

          <Button type="submit" className="w-full bg-blue-500 text-white hover:bg-blue-700" disabled={loading}>
            {loading ? 'Cargando...' : 'Iniciar sesión'}
          </Button>
        </form>

        <div className="flex justify-between items-center mt-4">
          <Button
            onClick={() => navigate('/register')}
            className="bg-gray-500 hover:bg-gray-700 text-white"
          >
            Registrarse
          </Button>
          <a href="#" onClick={() => alert('Funcionalidad de olvidé mi contraseña aún no implementada')} className="text-blue-500 text-sm hover:underline">
            ¿Olvidaste tu contraseña?
          </a>
        </div>
      </div>
    </div>
  );
};

export default Login;
