import React, { useState } from 'react';
import axios from '../axios';
import { useNavigate } from 'react-router-dom';
import Input from '../components/Input';
import Button from '../components/Button';
import './ProjectCreate.css';  // Agregar estilo CSS si es necesario

const ProjectCreate = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const handleCreateProject = async () => {
    try {
      await axios.post('/api/projects', { title, description });
      navigate('/projects');  // Redirige a la lista de proyectos
    } catch (error) {
      alert('Error al crear el proyecto');
    }
  };

  return (
    <div className="create-project-container">
      <h2>Crear Nuevo Proyecto</h2>
      <Input
        type="text"
        placeholder="Título del Proyecto"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <Input
        type="text"
        placeholder="Descripción"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <Button onClick={handleCreateProject}>Crear Proyecto</Button>
      <Button onClick={() => navigate('/projects')}>Cancelar</Button>
    </div>
  );
};

export default ProjectCreate;
