import React, { useState } from 'react';
import axios from '../axios'; // Asegúrate de que la ruta de axios es correcta
import { useHistory } from 'react-router-dom';

const ProjectCreate = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState(null);
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token'); // Si ya guardas el token en localStorage
      const response = await axios.post(
        '/api/projects', 
        { title, description }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Si el proyecto se crea correctamente, redirigimos al listado
      history.push('/projects');
    } catch (error) {
      setError('Error al crear el proyecto');
    }
  };

  return (
    <div>
      <h1>Crear Proyecto</h1>
      {error && <div className="error">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Título:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Descripción:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">Crear Proyecto</button>
      </form>
    </div>
  );
};

export default ProjectCreate;
