import React, { useState, useEffect } from 'react';
import axios from '../axios'; // Asegúrate de que la ruta es correcta

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Obtener los proyectos del backend
  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects');
      setProjects(response.data); // Asigna los proyectos a state
      setLoading(false);
    } catch (err) {
      setError('Error al obtener los proyectos');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjects(); // Cargar los proyectos al montar el componente
  }, []);

  if (loading) return <p>Cargando proyectos...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Mis Proyectos</h2>
      <div>
        {projects.length === 0 ? (
          <p>No tienes proyectos.</p>
        ) : (
          <ul>
            {projects.map((project) => (
              <li key={project._id}>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <p><strong>Fecha de creación:</strong> {new Date(project.createdAt).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ProjectList;
