import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from '../axios';
import Button from '../components/Button';
import Input from '../components/Input';
import '../styles/ProjectDetail.css'; // Asegúrate de que este archivo exista para los estilos.

const ProjectDetail = () => {
  const [project, setProject] = useState(null);
  const [showEditPopup, setShowEditPopup] = useState(false);
  const [updatedProjectData, setUpdatedProjectData] = useState({
    title: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);
  const { id } = useParams(); // Obtener el id del proyecto desde la URL
  const navigate = useNavigate();

  // Obtener los detalles del proyecto
  useEffect(() => {
    axios.get(`/api/projects/${id}`)
      .then(response => {
        setProject(response.data);
        setUpdatedProjectData({ title: response.data.title, description: response.data.description });
      })
      .catch(error => {
        console.error('Error al obtener el proyecto:', error);
      });
  }, [id]);

  // Manejo del cambio en los campos del formulario de edición
  const handleEditChange = (e) => {
    setUpdatedProjectData({ ...updatedProjectData, [e.target.name]: e.target.value });
  };

  // Enviar la actualización del proyecto
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.put(`/api/projects/${id}`, updatedProjectData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setProject(response.data); // Actualizar el estado con el proyecto editado
      setShowEditPopup(false); // Cerrar el popup
    } catch (error) {
      console.error('Error al editar el proyecto:', error);
    } finally {
      setLoading(false);
    }
  };

  // Manejo del cierre del popup
  const handleClosePopup = () => {
    setShowEditPopup(false);
  };

  return (
    <div className="project-detail-container">
      {project ? (
        <>
          <h2>{project.title}</h2>
          <p>{project.description}</p>

          <Button onClick={() => navigate('/projects')}>Volver a la lista de proyectos</Button>

          {/* Botón para abrir el popup de edición */}
          <Button onClick={() => setShowEditPopup(true)} className="edit-btn">
            Editar Proyecto
          </Button>

          {/* Popup de edición */}
          {showEditPopup && (
            <div className="edit-popup">
              <div className="popup-overlay" onClick={handleClosePopup}></div>
              <div className="popup-content">
                <h3>Editar Proyecto</h3>
                <form onSubmit={handleEditSubmit}>
                  <Input
                    type="text"
                    name="title"
                    value={updatedProjectData.title}
                    onChange={handleEditChange}
                    placeholder="Título del Proyecto"
                    className="mb-4"
                  />
                  <Input
                    type="text"
                    name="description"
                    value={updatedProjectData.description}
                    onChange={handleEditChange}
                    placeholder="Descripción del Proyecto"
                    className="mb-4"
                  />
                  <Button type="submit" disabled={loading} className="w-full">
                    {loading ? 'Guardando...' : 'Guardar Cambios'}
                  </Button>
                  <Button
                    type="button"
                    onClick={handleClosePopup}
                    className="w-full mt-2 bg-gray-500 hover:bg-gray-700"
                  >
                    Cancelar
                  </Button>
                </form>
              </div>
            </div>
          )}
        </>
      ) : (
        <p>Cargando...</p>
      )}
    </div>
  );
};

export default ProjectDetail;
